questionvariables.text:
/*Randomize each item individually ensuring integer values*/
random_rice: floor(rand(500 / 10)) * 10 + 120;
random_beans: floor(rand(300 / 10)) * 10 + 72;
random_vegetables: floor(rand(200 / 10)) * 10 + 48;
random_meat: floor(rand(400 / 10)) * 10 + 96;
random_sugar: floor(rand(100 / 10)) * 10 + 50;
random_maize: floor(rand(300 / 10)) * 10 + 72;

/* Compute total */
Total_values: random_rice + random_beans + random_vegetables + random_meat + random_sugar + random_maize;

/* Adjust angles to ensure whole numbers summing to 360 */
rice: floor((random_rice / Total_values) * 360);
beans: floor((random_beans / Total_values) * 360);
vegetables: floor((random_vegetables / Total_values) * 360);
meat: floor((random_meat / Total_values) * 360);
sugar: floor((random_sugar / Total_values) * 360);
maize: 360 - (rice + beans + vegetables + meat + sugar);


max_item: max(random_rice, random_beans, random_vegetables, random_meat, random_sugar, random_maize);

min_item: min(random_rice, random_beans, random_vegetables, random_meat, random_sugar, random_maize);

percentage_rice: decimalplaces((random_rice / Total_values) * 100,2);

sum_angles_rice_beans: rice + beans;

diff_meat_maize: meat - maize;

new_total: Total_values + random_vegetables;
new_angle_vegetables: decimalplaces((2 * random_vegetables / new_total) * 360,2);

diff_meat_beans: random_meat - random_beans;

fraction_maize: ratsimp(random_maize / Total_values);

sum_angles_meat_veg_sugar: meat + vegetables + sugar;

tans1:Total_values ;
tans2:max_item ;
tans3: min_item ;
tans4:percentage_rice ;
tans5: sum_angles_rice_beans ;
tans6:diff_meat_maize ;
tans7: new_angle_vegetables ;
tans14:diff_meat_beans ;
tans15: fraction_maize ;
tans16: sum_angles_meat_veg_sugar;
tans17: rice ;
tans18: beans ;
tans19: vegetables ;
tans20: meat ;
tans21: sugar ;
tans22: maize ;

feedbackvariables.text:
/* Node 1: Correct answer */


node1_test: if ans17=tans17 and ans18=tans18 and ans19=tans19 and ans20=tans20 and ans21=tans21 and ans22=tans22 then 1 else 0;

/* Node 2: Worked solution */

/* Node 3: ans17 correct */
/* Node 4: ans17 consistent with (a) */
node4_error: [float(rice), ans1];

